import numpy as np
import pandas as pd
from sklearn.model_selection import train_test_split
from sklearn.preprocessing import MinMaxScaler
from sklearn.linear_model import LogisticRegression
from sklearn.ensemble import RandomForestClassifier
from sklearn.metrics import confusion_matrix, classification_report,precision_recall_curve,roc_auc_score,roc_curve, auc,average_precision_score
from tensorflow.keras.models import Sequential
from tensorflow.keras.layers import Dense, Dropout, BatchNormalization
from tensorflow.keras.callbacks import EarlyStopping
from tensorflow.keras.optimizers import Adam
import matplotlib.pyplot as plt
from sklearn.utils.class_weight import compute_class_weight
from xgboost import XGBClassifier
from sklearn.naive_bayes import GaussianNB
from sklearn.svm import SVC
from imblearn.over_sampling import SMOTE


def scale_data(x_train,x_test):
    scaler = MinMaxScaler()
    return scaler.fit_transform(x_train),scaler.transform(x_test),scaler

def get_class_weight(y):
    class_weights = compute_class_weight(class_weight='balanced', classes=np.unique(y), y=y)
    return dict(zip(np.unique(y), class_weights))

def logistic_model(x_train,y_tarin,class_weight= None):
    model = LogisticRegression(max_iter=1000, random_state=42,class_weight=class_weight)
    model.fit(x_train,y_tarin)
    return model

def rf_model(x_train,y_train,class_weight=None):
    model = RandomForestClassifier(n_estimators=100, random_state=42,class_weight=class_weight)
    model.fit(x_train, y_train)
    return model

def gen_XGBoost_model(x_train_scaled,y_train):
    model = XGBClassifier(n_estimators=100, max_depth=3, learning_rate=0.1, random_state=42)
    model.fit(x_train_scaled, y_train)
    return model

def gen_naive_bayes_model(x_train_scaled, y_train):
    model = GaussianNB()
    model.fit(x_train_scaled, y_train)
    return model


def gen_evaluate_model_except_nn(model,x_test_scaled,y_test):
    pred = model.predict_proba(x_test_scaled)[:,1]
    roc_auc = roc_auc_score(y_test, pred)
    print(f"\n ROC-AUC Score: {roc_auc:.4f}")
    precision, recall, thresholds = precision_recall_curve(y_test, pred)
    f1_scores = 2 * (precision * recall) / (precision + recall + 1e-8)
    best_threshold = thresholds[np.argmax(f1_scores)]
    final_pred = (pred > best_threshold).astype("int32")
    print("\n Final Ensemble Confusion Matrix (Optimized Threshold):")
    print(confusion_matrix(y_test, final_pred))
    print("\n Final Ensemble Classification Report:")
    print(classification_report(y_test, final_pred))
    

def build_nn():
    model = Sequential()
    model.add(Dense(128, activation='relu'))
    model.add(BatchNormalization())
    model.add(Dropout(0.1))
    model.add(Dense(64, activation='relu'))
    model.add(BatchNormalization())
    model.add(Dropout(0.1))
    model.add(Dense(32, activation='relu'))
    model.add(BatchNormalization())
    model.add(Dropout(0.1))
    model.add(Dense(16, activation='relu'))
    model.add(BatchNormalization())
    model.add(Dropout(0.1))
    model.add(Dense(1, activation='sigmoid'))
    model.compile(loss="binary_crossentropy", optimizer=Adam(learning_rate=0.0001))
    return model

def gen_nn_model(x_train_scaled,y_train,x_test_scaled,y_test):
    earlystop = EarlyStopping(monitor='val_loss', mode='min', patience=20,restore_best_weights=True)
    model = build_nn()
    history = model.fit(x_train_scaled, y_train, validation_data=(x_test_scaled, y_test),
                        batch_size=256, epochs=200, callbacks=[earlystop],verbose=0)
    hist_df = pd.DataFrame(history.history)
    plt.figure(figsize=(8, 4))
    plt.plot(hist_df['loss'], label='Training Loss')
    plt.plot(hist_df['val_loss'], label='Validation Loss')
    plt.title('Model Loss')
    plt.xlabel('Epoch')
    plt.ylabel('Loss')
    plt.legend()
    plt.show()
    return model

def gen_evaluate_nn_model(model,x_test_scaled,y_test):
    pred = model.predict(x_test_scaled).flatten()
    roc_auc = roc_auc_score(y_test, pred)
    print(f"\n ROC-AUC Score: {roc_auc:.4f}")
    precision, recall, thresholds = precision_recall_curve(y_test, pred)
    f1_scores = 2 * (precision * recall) / (precision + recall + 1e-8)
    best_threshold = thresholds[np.argmax(f1_scores)]
    final_pred = (pred > best_threshold).astype("int32")
    print("\n Final Ensemble Confusion Matrix (Optimized Threshold):")
    print(confusion_matrix(y_test, final_pred))
    print("\n Final Ensemble Classification Report:")
    print(classification_report(y_test, final_pred))


def ensemble_nn_model(subsets,scaler):
    models =[]
    earlystop = EarlyStopping(monitor='val_loss', mode='min', patience=20)
    for i,subset in enumerate(subsets):
        x = subset.drop("loan_repaid", axis=1).values
        y = subset["loan_repaid"].values
