        x_train_sub_scaled = scaler.transform(x_train_sub)  # Use transform, not fit_transform
        x_test_sub_scaled = scaler.transform(x_test_sub)
        model = build_nn()
        history = model.fit(x_train_sub_scaled, y_train_sub, validation_data=(x_test_sub_scaled, y_test_sub),
                  batch_size=256, epochs=200, callbacks=[earlystop],verbose=0)
