def evaluate_model_voting(lr_pred, rf_pred,xg_pred, nn_pred,naive_pred, y_test):
    final_pred_proba = (lr_pred + rf_pred + xg_pred + nn_pred + naive_pred) / 5
    roc_auc = roc_auc_score(y_test, final_pred_proba)
    print(f"\n ROC-AUC Score: {roc_auc:.4f}")

    precision, recall, thresholds = precision_recall_curve(y_test, final_pred_proba)
    f1_scores = 2 * (precision * recall) / (precision + recall + 1e-8)
    best_threshold = thresholds[np.argmax(f1_scores)]
    print(f"\n Best threshold for F1: {best_threshold:.4f}")

    final_pred = (final_pred_proba > best_threshold).astype("int32")
    print("\n Final Ensemble Confusion Matrix (Optimized Threshold):")
    print(confusion_matrix(y_test, final_pred))
    print("\n Final Ensemble Classification Report:")
    print(classification_report(y_test, final_pred))

def ensemble_evaluate_model_voting(ensemble_lr, ensemble_rf, ensemble_XGBoost,ensemble_nn_subset, x_test_scaled, y_test):
    ensemble_nn_pred_subset = [model.predict(x_test_scaled).flatten() for model in ensemble_nn_subset]
    ensemble_nn_proba = np.mean(ensemble_nn_pred_subset, axis=0)
    ensemble_rf_pred_subset = [model.predict_proba(x_test_scaled)[:, 1] for model in ensemble_rf]
    ensemble_rf_proba = np.mean(ensemble_rf_pred_subset, axis=0)
    ensemble_xg_pred_subset = [model.predict_proba(x_test_scaled)[:, 1] for model in ensemble_XGBoost]
    ensemble_xg_proba = np.mean(ensemble_xg_pred_subset, axis=0)
    ensemble_lr_pred_subset = [model.predict_proba(x_test_scaled)[:, 1] for model in ensemble_lr]
    ensemble_lr_proba = np.mean(ensemble_lr_pred_subset, axis=0)
    

    final_pred_proba = (ensemble_nn_proba + ensemble_rf_proba + ensemble_xg_proba + ensemble_lr_proba) / 4
    roc_auc = roc_auc_score(y_test, final_pred_proba)
    print(f"\n ROC-AUC Score: {roc_auc:.4f}")
    
    precision, recall, thresholds = precision_recall_curve(y_test, final_pred_proba)
    f1_scores = 2 * (precision * recall) / (precision + recall + 1e-8)
    best_threshold = thresholds[np.argmax(f1_scores)]
    print(f"\n Best threshold for F1: {best_threshold:.4f}")

    final_pred = (final_pred_proba > best_threshold).astype("int32")
    print("\n Final Ensemble Confusion Matrix (Optimized Threshold):")
    print(confusion_matrix(y_test, final_pred))
    print("\n Final Ensemble Classification Report:")
    print(classification_report(y_test, final_pred))

def smote_sampling(df,random_state=42):
    x = df.drop("loan_repaid", axis=1).values
    y = df["loan_repaid"].values
    x_train, x_test, y_train, y_test = train_test_split(x, y, test_size=0.2, stratify=y, random_state=random_state)
    smote = SMOTE(random_state = random_state)
    x_train_res,y_train_res = smote.fit_resample(x_train,y_train)
    x_train_scaled, x_test_scaled, scaler = scale_data(x_train_res, x_test)
    return x_train_scaled,y_train_res,x_test_scaled,y_test,scaler


def gen_evaluate_model_ROC_AUC_from_predictions(lr_pred, rf_pred,xg_pred, nn_pred, y_test):
    pred_dict = {
    "Logistic Regression": lr_pred,
    "Random Forest": rf_pred,
    "XGBoost": xg_pred,
    "Neural Net": nn_pred}
    
    plt.figure(figsize=(8, 6))
    for name, y_pred_proba in pred_dict.items():
        fpr, tpr, _ = roc_curve(y_test, y_pred_proba)
